import { Sku } from "../reducers/cart"
import { AppDispatch } from "../store"

export const ADD_CART_ITEM = "@@cart/cartItemAdded"
export const REMOVE_CART_ITEM = "@@cart/cartItemRemoved"

export const addCartItem = (sku: Sku) => {
    return async (dispatch: AppDispatch) => ({
        type: ADD_CART_ITEM,
        payload: {
            sku: sku,
        }
    })
}

export const removeCartItem = (sku: Sku, quantity: number) => {
    return async (dispatch: AppDispatch) => ({
        type: REMOVE_CART_ITEM,
        payload: {
            sku: sku,
            quantity: quantity,
        }
    })
}
