import { UnknownAction } from "@reduxjs/toolkit"
import { Sku } from "./cart"

export type User = {
    // PERSONAL INFO
    firstName: string
    lastName: string

    // CONTACT INFO
    email: string
    password: string
    phone: string

    // ADDRESS INFO
    address: string
    city: string
    state: string
    country: string
    zipcode: string

    // OPTIONAL INFO
    age?: number
    gender?: string
    middleInitial?: string

    // USER INFO
    totalSpent: number
    numPurchases: number
    purchases: Sku[]

    // MISC
    id: number
    userSince: Date
    onboarded: boolean
}

export type UserState = {
    user?: User
    loading: boolean
    error?: Error
}

const InitialState: UserState = {
    user: undefined,
    error: undefined,
    loading: false,
}

export default (state = InitialState, action: UnknownAction): UserState => {
